import React from "react";

class Editfishform extends React.Component
{   
    handleChange = event => {
        console.log(event.currentTarget.value);
        // update that fish
        // 1. Take a copy of the curernt fish
        const updatedFish = {
          ...this.props.fish,
          [event.currentTarget.name]: event.currentTarget.value
        };
        this.props.updateFish(this.props.index, updatedFish); //function defined in app.js
      };
    render()
    {
        return(
<div className="fish-edit">
        <input
          type="text"
          name="name"
          onChange={this.handleChange}// to change the default fish in the form that is update a new fish and add it to the state present in app
          value={this.props.fish.name}
        />
        <input
          type="text"
          name="price"
          onChange={this.handleChange}
          value={this.props.fish.price}
        />
        <select
          type="text"
          name="status"
          onChange={this.handleChange}
          value={this.props.fish.status}
        >
          <option value="available">Fresh!</option>
          <option value="unavailable">Sold Out!</option>
        </select>
        <textarea
          name="desc"
          onChange={this.handleChange}
          value={this.props.fish.desc}
        />
        <input
          type="text"
          name="image"
          onChange={this.handleChange}
          value={this.props.fish.image}
        />
      <button>Remove Fish</button>
      </div>
        );
    }
}

export default Editfishform;
